import Web3 from "web3"
import { web3Loaded, web3AccountLoaded, web3NetworkIdLoaded, web3TokenLoaded } from "./actions"

export const loadWeb3 = (dispatch) => {
    const web3 = new Web3(Web3.givenProvider || 'http://localhost:7545')
    dispatch(web3Loaded(web3))
    return web3
}

export const loadAccount = async (web3, dispatch) => {
    const accounts =  await web3.eth.getAccounts()
    const account = accounts[0]
    dispatch(web3AccountLoaded(account))
    return account
}

export const loadToken = async (web3, dispatch, networkId) => {
    const token =  new web3.eth.Contract(Token.abi, Token.networks[networkId].address)
    dispatch(web3TokenLoaded(token))
    return token
}

export const loadNetworkId = async (web3, dispatch) => {
    const networkId =  await web3.eth.net.getId()
    dispatch(web3NetworkIdLoaded(networkId))
    return networkId
}
