export function web3Loaded(connection) {
    return { 
        type: 'WEB3_LOADED',
        connection
    }
}

export function web3AccountLoaded(account) {
    return { 
        type: 'WEB3_ACCOUNT_LOADED',
        account
    }
}

export function web3NetworkIdLoaded(networkId) {
    return { 
        type: 'WEB3_NETWORK_ID_LOADED',
        networkId
    }
}
